import React, { useEffect, useState } from 'react'
import './newsletter.component.scss'
import NewsForm from '../NewsForm/newsform.component'
import {ReactComponent as AirPlane} from '../../assets/art-send.svg'
import { useAnimation } from "framer-motion"
import { useInView } from 'react-intersection-observer'
import { motion } from "framer-motion"

export default function Newsletter() {

    const [click, setClick] = useState(false)
    console.log(click)

    const handleClick = () => {
        setClick(!click)
    }

    const animation = useAnimation()
    const [newsRef, inView] = useInView({
        triggerOnce: true,
        rootMargin: '-100px'
    })

    useEffect( () => {
        if(inView) {
            animation.start("visible")
        }
    }, [animation, inView])

    return (
        <section className="section section-04">
            <div className="container">
                <div className="newsletter-message">
                    <div className="display-2"><h1>Newsletter</h1></div>
                    <p>Abonează-te la newsletter-ul nostru și primești cele mai noi știri din domeniu cât si promoțiile noastre.</p>
                    <NewsForm click={click} handleClick={handleClick}/>
                    <h5 style={ click ? {display: "block"} : {display: "none"} }>Multumim pentru abonare!</h5>
                </div>"
                <motion.div 
                    className="air" 
                    ref={newsRef}
                    animate={animation}
                    initial="hidden"
                    variants={{
                        hidden: { 
                            opacity: 0, 
                            x: '-200px',
                            y: "200px"
                        },
                        visible: { 
                            opacity: 1, 
                            x: 0,
                            y: 0,
                            transition:{
                                ease: "easeOut"
                            }
                        }
                    }}
                    >
                    <AirPlane/>
                </motion.div>
            </div>
        </section>
    )
}
