import React, { useEffect } from 'react'
import './contact.component.scss'
import { ReactComponent as FacebookLogo } from '../../assets/facebook.svg';
import { ReactComponent as YoutubeLogo } from '../../assets/youtube.svg';
import { ReactComponent as LocationLogo } from '../../assets/location.svg';
import { useAnimation } from "framer-motion"
import { useInView } from 'react-intersection-observer'
import { motion } from "framer-motion"

export default function Contact() {

    const animation = useAnimation()
    const [contactRef, inView] = useInView({
        triggerOnce: true
    })

    useEffect( () => {
        if(inView) {
            animation.start("visible")
        }
    }, [animation, inView])

    return (
        <section className="section section-06">
            <div className="container">
                <div className="contact"> 
                    <div className="contact-body">
                        <div className="social">
                            <h2>Follow us</h2>
                            <div className="social-icons"> 
                                <motion.div 
                                className="social-icons"
                                ref={contactRef}
                                animate={animation}
                                initial="hidden"
                                variants={{
                                    hidden: { 
                                        opacity: 0,
                                        scale: 0
                                    },
                                    visible: { 
                                        opacity: 1,
                                        scale: 1
                                    }
                                }}
                                >
                                    <a
                                        href="https://www.facebook.com/janmarcom/"
                                    >
                                        <FacebookLogo/>
                                    </a>
                                    <a
                                        href="https://www.youtube.com/channel/UCucNz2AyKE7zWhUCtfV2DUA"
                                    >
                                        <YoutubeLogo/>
                                    </a>
                                </motion.div>
                            </div>
                        </div>
                        <div className="locations" >
                            <h2>Contact</h2>
                            <div className="location">
                                <h4>depozit fagaras</h4>
                                <div className="location-body">
                                    <div className="location-descriptiom">
                                        <p>Str. Doamna Stanca nr. 76</p>
                                        <p>Fagaras - Jud. Brasov</p>
                                        <p>email: janmar.com.srl@gmail.com</p>
                                        <p>tel: +40371169607</p>
                                    </div>
                                    <motion.div 
                                    className="location-icon"
                                    ref={contactRef}
                                    animate={animation}
                                    initial="hidden"
                                    variants={{
                                        hidden: { 
                                            opacity: 0,
                                            scale: 0
                                        },
                                        visible: { 
                                            opacity: 1,
                                            scale: 1,
                                            transition: {
                                                delay:0.1
                                            }
                                        }
                                    }}
                                    >   
                                        <a href="https://goo.gl/maps/w5dcqxZRwoYdhBtq6">
                                            <LocationLogo/>
                                        </a>
                                    </motion.div>
                                </div>
                            </div>
                            <div className="location">
                                <h4>depozit oltet</h4>
                                <div className="location-body">
                                    <div className="location-descriptiom">
                                        <p>Com. Vistea de Jos - Intersectia cu Rucar</p>
                                        <p>Jud. Brasov</p>
                                        <p>email: janmarcom.oltet@gmail.com</p>
                                        <p>tel: +4032409951</p>
                                    </div>
                                    <motion.div 
                                    className="location-icon"
                                    ref={contactRef}
                                    animate={animation}
                                    initial="hidden"
                                    variants={{
                                        hidden: { 
                                            opacity: 0,
                                            scale: 0
                                        },
                                        visible: { 
                                            opacity: 1,
                                            scale: 1,
                                            transition: {
                                                delay:0.2
                                            }
                                        }
                                    }}
                                    >
                                        <a href="https://goo.gl/maps/3gA8xFZ5Qr1BAdBg6">
                                            <LocationLogo/>
                                        </a>
                                    </motion.div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    )
}
