import React, { useEffect } from 'react'
import './vopsele.component.scss'
import { useAnimation } from "framer-motion"
import { useInView } from 'react-intersection-observer'
import { motion } from "framer-motion"

export default function Vopsele() {

    const animation = useAnimation()
    const [vopseleRef, inView] = useInView({
        triggerOnce: true,
        rootMargin: '-100px'
    })

    useEffect( () => {
        if(inView) {
            animation.start("visible")
        }
    }, [animation, inView])

    return (
        <motion.div 
        className="vopsele-message"
        ref={vopseleRef}
        animate={animation}
        initial="hidden"
        variants={{
            hidden: { 
                opacity: 0, 
                y: 200 
            },
            visible: { 
                opacity: 1, 
                y: 0 
            }
        }}
        >
            <h1>Mixare vopsele & tencuieli exterioare</h1>
            <p>Te ajutăm să obții nuanța de vopsea sau tencuială pe care o dorești</p>
        </motion.div>
    )
}
