import React, { useState } from 'react'
import './navigation.component.scss'
import { motion } from "framer-motion"
import Logo from '../../components/logo/logo.component'
import Menu from '../../components/Menu/Menu.component'
import { Link, useHistory } from 'react-router-dom'

export default function Navigation() {

    const history = useHistory()
    const navigateTo = () => history.push('/')

    const [showMenu, setShowMenu] = useState(false)

    const menuVariants = {
        hidden: {
            top: '-100vh'
        },
        visible: {
            top: 0,
            transition: {
                duration: 0.5
            }
        }
    }

    const up = {
        y: '50%',
        rotate: 45,
        originY: 'top',
        transition:{
            duration: 3
        }
    }

    let menu

    if(showMenu){
        menu =  
        <motion.div 
        className="collapse-area" 
        onClick={() => setShowMenu(false)}
        initial={"hidden"}
        animate={ showMenu ? "visible" : "hidden"}
        variants={menuVariants}
        >
            <Menu/>
        </motion.div>
    }

    return (
        <nav>
            <div className="navigation">
                <Link 
                to="/" 
                onClick={() => {
                    navigateTo()
                    window.location.reload(false)
                }}
                >
                    <Logo/>
                </Link>
                <button onClick={() => setShowMenu(!showMenu)}>
                    <svg width="31" height="30" viewBox="0 0 31 30" fill="none" xmlns="http://www.w3.org/2000/svg">
                        <motion.path id="path01" d="M31 0.470825H0.294922V6.15695H31V0.470825Z" whileTap={up}/>
                        <motion.path id="path02" d="M31 12.1569H0.294922V17.843H31V12.1569Z" animate={{opacity: 0}}/>
                        <motion.path id="path03" d="M31 23.843H0.294922V29.5291H31V23.843Z" animate={{rotate: -45}}/>
                    </svg>
                </button>
            </div>
            {menu}
        </nav>
    )
}
